mapper.insert:
insert into app_info(softwareName, APKName,
                     supportROM, interfaceLanguage,
                     softwareSize, updateDate, devId,
                     appInfo, status, onSaleDate,
                     offSaleDate, flatformId,
                     categoryLevel3, downloads, createdBy,
                     creationDate, logoPicPath,
                     logoLocPath, versionId)
values (#{softwareName}, #{APKName}, #{supportROM}
    #{interfaceLanguage}, #{softwareSize}, #{updateDate}
            #{devId}, #{appInfo}, #{status}
            #{onSaleDate}, #{offSaleDate}, #{flatformId}
            #{categoryLevel3}, #{downloads}, #{createdBy}
            #{creationDate}, #{logoPicPath}, #{logoLocPath}
            #{versionId})

mapper.select:
select * from (SELECT c1c.categoryName categoryName1, c1.* FROM ( SELECT c2c.categoryName categoryName2, c2.* FROM ( SELECT c3c.categoryName categoryName3, c3.* FROM ( SELECT d2.valueName flatformName, v2.* FROM ( SELECT d.valueName statusName, v1.* FROM ( SELECT v.versionNo, i.* FROM app_info i LEFT JOIN app_version v ON i.versionId = v.id ) v1 LEFT JOIN data_dictionary d ON v1.STATUS = d.valueId AND d.typeCode = 'APP_STATUS' ) v2 LEFT JOIN data_dictionary d2 ON v2.flatformId = d2.valueId AND d2.typeCode = 'APP_FLATFORM' ) c3 LEFT JOIN app_category c3c ON c3.categoryLevel3 = c3c.id ) c2 LEFT JOIN app_category c2c ON c2.categoryLevel2 = c2c.id ) c1 LEFT JOIN app_category c1c ON c1.categoryLevel1 = c1c.id) i3
1=1
and softwareName like concat('%',#{appName},'%')
and status = #{status}
and flatformId = #{flatformId}
and categoryLevel1 = #{categoryLevel1}
and categoryLevel2 = #{categoryLevel2}
and categoryLevel3 = #{categoryLevel3}

SELECT categoryName
FROM app_category
WHERE id = #{categoryLevel}
